import { useState, useEffect } from 'react';
import { db } from '../firebase-config';
import { collection, addDoc, query, getDocs, updateDoc, doc } from 'firebase/firestore';

function Inventory() {
  const [items, setItems] = useState([]);
  const [newItem, setNewItem] = useState({
    sku: '',
    name: '',
    price: '',
    quantity: 1,
    consignorId: '',
    status: 'available', // available, sold, pending
    dateAdded: new Date(),
    description: ''
  });

  useEffect(() => {
    fetchItems();
  }, []);

  const fetchItems = async () => {
    const q = query(collection(db, 'inventory'));
    const querySnapshot = await getDocs(q);
    const itemsList = [];
    querySnapshot.forEach((doc) => {
      itemsList.push({ id: doc.id, ...doc.data() });
    });
    setItems(itemsList);
  };

  const handleAddItem = async (e) => {
    e.preventDefault();
    try {
      await addDoc(collection(db, 'inventory'), {
        ...newItem,
        price: parseFloat(newItem.price),
        quantity: parseInt(newItem.quantity),
        dateAdded: new Date()
      });
      setNewItem({
        sku: '',
        name: '',
        price: '',
        quantity: 1,
        consignorId: '',
        status: 'available',
        dateAdded: new Date(),
        description: ''
      });
      fetchItems();
    } catch (error) {
      console.error("Error adding item: ", error);
    }
  };

  const markAsSold = async (itemId) => {
    try {
      const itemRef = doc(db, 'inventory', itemId);
      await updateDoc(itemRef, {
        status: 'sold',
        dateSold: new Date()
      });
      fetchItems();
    } catch (error) {
      console.error("Error updating item: ", error);
    }
  };

  return (
    <div className="inventory-container">
      <div className="add-item-form">
        <h2>Add New Item</h2>
        <form onSubmit={handleAddItem}>
          <input
            type="text"
            placeholder="SKU"
            value={newItem.sku}
            onChange={(e) => setNewItem({...newItem, sku: e.target.value})}
            required
          />
          <input
            type="text"
            placeholder="Item Name"
            value={newItem.name}
            onChange={(e) => setNewItem({...newItem, name: e.target.value})}
            required
          />
          <input
            type="number"
            placeholder="Price"
            value={newItem.price}
            onChange={(e) => setNewItem({...newItem, price: e.target.value})}
            required
          />
          <input
            type="number"
            placeholder="Quantity"
            value={newItem.quantity}
            onChange={(e) => setNewItem({...newItem, quantity: e.target.value})}
            required
          />
          <input
            type="text"
            placeholder="Consignor ID"
            value={newItem.consignorId}
            onChange={(e) => setNewItem({...newItem, consignorId: e.target.value})}
            required
          />
          <textarea
            placeholder="Description"
            value={newItem.description}
            onChange={(e) => setNewItem({...newItem, description: e.target.value})}
          />
          <button type="submit">Add Item</button>
        </form>
      </div>

      <div className="inventory-list">
        <h2>Current Inventory</h2>
        <table>
          <thead>
            <tr>
              <th>SKU</th>
              <th>Name</th>
              <th>Price</th>
              <th>Quantity</th>
              <th>Status</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {items.map((item) => (
              <tr key={item.id}>
                <td>{item.sku}</td>
                <td>{item.name}</td>
                <td>${item.price}</td>
                <td>{item.quantity}</td>
                <td>{item.status}</td>
                <td>
                  {item.status === 'available' && (
                    <button onClick={() => markAsSold(item.id)}>Mark as Sold</button>
                  )}
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
}

export default Inventory;
