import { useState, useEffect, useRef } from 'react';
import { db } from '../firebase-config';
import { collection, addDoc, query, getDocs } from 'firebase/firestore';

function Inventory() {
  const [items, setItems] = useState([]);
  const [showAddForm, setShowAddForm] = useState(false);
  const [searchTerm, setSearchTerm] = useState('');
  const [searchType, setSearchType] = useState('sku');
  const [sortField, setSortField] = useState('');
  const [showSortOptions, setShowSortOptions] = useState(false);
  const [newItem, setNewItem] = useState({
    sku: '',
    name: '',
    price: '',
    quantity: 1,
    description: '',
    consignorName: '',
    dateAdded: new Date(),
    status: 'available'
  });
  const filterRef = useRef(null);

  useEffect(() => {
    fetchItems();
  }, []);

  useEffect(() => {
    const handleClickOutside = (event) => {
      if (filterRef.current && !filterRef.current.contains(event.target)) {
        setShowSortOptions(false);
      }
    };

    document.addEventListener('mousedown', handleClickOutside);
    return () => {
      document.removeEventListener('mousedown', handleClickOutside);
    };
  }, []);

  const fetchItems = async () => {
    try {
      const q = query(collection(db, 'inventory'));
      const querySnapshot = await getDocs(q);
      const itemsList = [];
      querySnapshot.forEach((doc) => {
        itemsList.push({ id: doc.id, ...doc.data() });
      });
      setItems(itemsList);
    } catch (error) {
      console.error('Error fetching items:', error.message);
    }
  };

  const handleAddItem = async (e) => {
    e.preventDefault();
    try {
      await addDoc(collection(db, 'inventory'), {
        ...newItem,
        price: parseFloat(newItem.price),
        quantity: parseInt(newItem.quantity),
        dateAdded: new Date()
      });

      setNewItem({
        sku: '',
        name: '',
        price: '',
        quantity: 1,
        description: '',
        consignorName: '',
        dateAdded: new Date(),
        status: 'available'
      });

      setShowAddForm(false);
      fetchItems();
    } catch (error) {
      console.error('Error adding item:', error.message);
    }
  };

  const getSortedItems = (items) => {
    if (!sortField) return items;

    return [...items].sort((a, b) => {
      if (sortField === 'date') {
        return new Date(b.dateAdded) - new Date(a.dateAdded);
      } else if (sortField === 'price') {
        return b.price - a.price;
      }
      return 0;
    });
  };

  const filteredItems = getSortedItems(items.filter(item => {
    if (!searchTerm) return true;

    const searchLower = searchTerm.toLowerCase();
    return item.sku.toLowerCase().includes(searchLower) || 
           item.consignorName.toLowerCase().includes(searchLower);
  }));

  return (
    <div className="inventory-page">
      <div className="controls-bar">
        <div className="search-controls">
          <input
            type="text"
            placeholder="Search by SKU or consignor name"
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
            className="search-input"
          />
          <button 
            onClick={() => setShowSortOptions(!showSortOptions)} 
            className="search-type"
          >
            Filter
          </button>
        </div>
        <div className="filter-controls" ref={filterRef}>
          <button 
            onClick={() => setShowSortOptions(!showSortOptions)} 
            className="filter-button"
          >
            Filter
          </button>
          {showSortOptions && (
            <div className="sort-dropdown">
              <button 
                onClick={() => setSortField('date')}
                className={sortField === 'date' ? 'active' : ''}
              >
                Date (Newest First)
              </button>
              <button 
                onClick={() => setSortField('price')}
                className={sortField === 'price' ? 'active' : ''}
              >
                Price (High to Low)
              </button>
              {sortField && (
                <button 
                  onClick={() => setSortField('')}
                  className="clear-sort"
                >
                  Clear Sort
                </button>
              )}
            </div>
          )}
        </div>
        <button onClick={() => setShowAddForm(!showAddForm)} className="add-button">
          {showAddForm ? 'Cancel' : 'Add New Item'}
        </button>
      </div>

      {showAddForm && (
        <div className="add-item-form">
          <h3>Add New Item</h3>
          <form onSubmit={handleAddItem}>
            <div className="form-grid">
              <input
                type="text"
                placeholder="SKU"
                value={newItem.sku}
                onChange={(e) => setNewItem({...newItem, sku: e.target.value})}
                required
              />
              <input
                type="text"
                placeholder="Item Name"
                value={newItem.name}
                onChange={(e) => setNewItem({...newItem, name: e.target.value})}
                required
              />
              <input
                type="number"
                step="0.01"
                placeholder="Price"
                value={newItem.price}
                onChange={(e) => setNewItem({...newItem, price: e.target.value})}
                required
              />
              <input
                type="number"
                placeholder="Quantity"
                value={newItem.quantity}
                onChange={(e) => setNewItem({...newItem, quantity: e.target.value})}
                required
              />
              <input
                type="text"
                placeholder="Consignor Name"
                value={newItem.consignorName}
                onChange={(e) => setNewItem({...newItem, consignorName: e.target.value})}
                required
              />
              <textarea
                placeholder="Description"
                value={newItem.description}
                onChange={(e) => setNewItem({...newItem, description: e.target.value})}
              />
            </div>
            <button type="submit">Add Item</button>
          </form>
        </div>
      )}

      <div className="inventory-table">
        <table>
          <thead>
            <tr>
              <th>SKU</th>
              <th>Name</th>
              <th>Price</th>
              <th>Quantity</th>
              <th>Consignor</th>
              <th>Status</th>
              <th>Date Added</th>
            </tr>
          </thead>
          <tbody>
            {filteredItems.map((item) => (
              <tr key={item.id}>
                <td>{item.sku}</td>
                <td>{item.name}</td>
                <td>${item.price}</td>
                <td>{item.quantity}</td>
                <td>{item.consignorName}</td>
                <td>{item.status}</td>
                <td>{new Date(item.dateAdded).toLocaleDateString()}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
}

export default Inventory;
