import { useState, useEffect } from 'react';
import { db } from '../firebase-config';
import { collection, addDoc, query, getDocs } from 'firebase/firestore';

function Inventory() {
  const [items, setItems] = useState([]);
  const [showAddForm, setShowAddForm] = useState(false);
  const [error, setError] = useState('');
  const [newItem, setNewItem] = useState({
    sku: '',
    name: '',
    price: '',
    quantity: 1,
    description: '',
    consignorName: '',
    dateAdded: new Date(),
    status: 'available'
  });

  useEffect(() => {
    fetchItems();
  }, []);

  const fetchItems = async () => {
    try {
      const q = query(collection(db, 'inventory'));
      const querySnapshot = await getDocs(q);
      const itemsList = [];
      querySnapshot.forEach((doc) => {
        itemsList.push({ id: doc.id, ...doc.data() });
      });
      setItems(itemsList);
      console.log('Fetched items:', itemsList); // Debug log
    } catch (error) {
      console.error("Error fetching items:", error);
      setError('Error fetching items: ' + error.message);
    }
  };

  const handleAddItem = async (e) => {
    e.preventDefault();
    setError('');
    console.log('Attempting to add item:', newItem); // Debug log

    try {
      const docRef = await addDoc(collection(db, 'inventory'), {
        ...newItem,
        price: parseFloat(newItem.price),
        quantity: parseInt(newItem.quantity),
        dateAdded: new Date()
      });

      console.log('Document written with ID:', docRef.id); // Debug log

      // Reset form
      setNewItem({
        sku: '',
        name: '',
        price: '',
        quantity: 1,
        description: '',
        consignorName: '',
        dateAdded: new Date(),
        status: 'available'
      });

      setShowAddForm(false);
      fetchItems(); // Refresh the list

    } catch (error) {
      console.error("Error adding item:", error);
      setError('Error adding item: ' + error.message);
    }
  };

  return (
    <div className="inventory-page">
      <div className="inventory-header">
        <h2>Inventory Items</h2>
        <button onClick={() => setShowAddForm(!showAddForm)} className="add-button">
          {showAddForm ? 'Cancel' : 'Add New Item'}
        </button>
      </div>

      {error && <div className="error-message">{error}</div>}

      {showAddForm && (
        <div className="add-item-form">
          <h3>Add New Item</h3>
          <form onSubmit={handleAddItem}>
            <div className="form-grid">
              <input
                type="text"
                placeholder="SKU"
                value={newItem.sku}
                onChange={(e) => setNewItem({...newItem, sku: e.target.value})}
                required
              />
              <input
                type="text"
                placeholder="Item Name"
                value={newItem.name}
                onChange={(e) => setNewItem({...newItem, name: e.target.value})}
                required
              />
              <input
                type="number"
                step="0.01"
                placeholder="Price"
                value={newItem.price}
                onChange={(e) => setNewItem({...newItem, price: e.target.value})}
                required
              />
              <input
                type="number"
                placeholder="Quantity"
                value={newItem.quantity}
                onChange={(e) => setNewItem({...newItem, quantity: e.target.value})}
                required
              />
              <input
                type="text"
                placeholder="Consignor Name"
                value={newItem.consignorName}
                onChange={(e) => setNewItem({...newItem, consignorName: e.target.value})}
                required
              />
              <textarea
                placeholder="Description"
                value={newItem.description}
                onChange={(e) => setNewItem({...newItem, description: e.target.value})}
              />
            </div>
            <button type="submit" className="submit-button">Add Item</button>
          </form>
        </div>
      )}

      <div className="inventory-grid">
        {items.map((item) => (
          <div key={item.id} className="inventory-item">
            <h3>{item.name}</h3>
            <p>SKU: {item.sku}</p>
            <p>Price: ${item.price}</p>
            <p>Quantity: {item.quantity}</p>
            <p>Status: {item.status}</p>
            <p>Consignor: {item.consignorName}</p>
            {item.description && <p>Description: {item.description}</p>}
          </div>
        ))}
      </div>
    </div>
  );
}

export default Inventory;
