import { useState, useEffect } from 'react';
import { db } from '../firebase-config';
import { collection, addDoc, query, getDocs } from 'firebase/firestore';

function Inventory() {
  const [items, setItems] = useState([]);
  const [showAddForm, setShowAddForm] = useState(false);
  const [error, setError] = useState('');
  const [sortBy, setSortBy] = useState('dateAdded');
  const [sortOrder, setSortOrder] = useState('desc');
  const [searchTerm, setSearchTerm] = useState('');
  const [searchType, setSearchType] = useState('sku'); // 'sku' or 'consignor'
  const [newItem, setNewItem] = useState({
    sku: '',
    name: '',
    price: '',
    quantity: 1,
    description: '',
    consignorName: '',
    dateAdded: new Date(),
    status: 'available'
  });

  useEffect(() => {
    fetchItems();
  }, []);

  const fetchItems = async () => {
    try {
      const q = query(collection(db, 'inventory'));
      const querySnapshot = await getDocs(q);
      const itemsList = [];
      querySnapshot.forEach((doc) => {
        itemsList.push({ id: doc.id, ...doc.data() });
      });
      setItems(itemsList);
    } catch (error) {
      setError('Error fetching items: ' + error.message);
    }
  };

  const sortItems = (items) => {
    return [...items].sort((a, b) => {
      switch (sortBy) {
        case 'dateAdded':
          return sortOrder === 'desc' 
            ? new Date(b.dateAdded) - new Date(a.dateAdded)
            : new Date(a.dateAdded) - new Date(b.dateAdded);
        case 'price':
          return sortOrder === 'desc' 
            ? b.price - a.price
            : a.price - b.price;
        default:
          return 0;
      }
    });
  };

  const filterItems = (items) => {
    if (!searchTerm) return items;
    return items.filter(item => {
      if (searchType === 'sku') {
        return item.sku.toLowerCase().includes(searchTerm.toLowerCase());
      } else {
        return item.consignorName.toLowerCase().includes(searchTerm.toLowerCase());
      }
    });
  };

  const displayedItems = filterItems(sortItems(items));

  return (
    <div className="inventory-page">
      <div className="controls-bar">
        <div className="search-controls">
          <input
            type="text"
            placeholder={`Search by ${searchType}...`}
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
            className="search-input"
          />
          <select 
            value={searchType}
            onChange={(e) => setSearchType(e.target.value)}
            className="search-type"
          >
            <option value="sku">SKU</option>
            <option value="consignor">Consignor</option>
          </select>
        </div>
        <div className="sort-controls">
          <select 
            value={sortBy}
            onChange={(e) => setSortBy(e.target.value)}
            className="sort-select"
          >
            <option value="dateAdded">Date Added</option>
            <option value="price">Price</option>
          </select>
          <button 
            onClick={() => setSortOrder(sortOrder === 'desc' ? 'asc' : 'desc')}
            className="sort-order"
          >
            {sortOrder === 'desc' ? '↓' : '↑'}
          </button>
        </div>
        <button onClick={() => setShowAddForm(!showAddForm)} className="add-button">
          {showAddForm ? 'Cancel' : 'Add New Item'}
        </button>
      </div>

      {/* Add Form */}
      {showAddForm && (
        <div className="add-item-form">
          <h3>Add New Item</h3>
          <form onSubmit={handleAddItem}>
            <div className="form-grid">
              <input
                type="text"
                placeholder="SKU"
                value={newItem.sku}
                onChange={(e) => setNewItem({...newItem, sku: e.target.value})}
                required
              />
              <input
                type="text"
                placeholder="Item Name"
                value={newItem.name}
                onChange={(e) => setNewItem({...newItem, name: e.target.value})}
                required
              />
              <input
                type="number"
                step="0.01"
                placeholder="Price"
                value={newItem.price}
                onChange={(e) => setNewItem({...newItem, price: e.target.value})}
                required
              />
              <input
                type="number"
                placeholder="Quantity"
                value={newItem.quantity}
                onChange={(e) => setNewItem({...newItem, quantity: e.target.value})}
                required
              />
              <input
                type="text"
                placeholder="Consignor Name"
                value={newItem.consignorName}
                onChange={(e) => setNewItem({...newItem, consignorName: e.target.value})}
                required
              />
              <textarea
                placeholder="Description"
                value={newItem.description}
                onChange={(e) => setNewItem({...newItem, description: e.target.value})}
              />
            </div>
            <button type="submit" className="submit-button">Add Item</button>
          </form>
        </div>
      )}

      {/* Inventory Table */}
      <div className="inventory-table">
        <table>
          <thead>
            <tr>
              <th>SKU</th>
              <th>Name</th>
              <th>Price</th>
              <th>Quantity</th>
              <th>Consignor</th>
              <th>Status</th>
              <th>Date Added</th>
            </tr>
          </thead>
          <tbody>
            {displayedItems.map((item) => (
              <tr key={item.id}>
                <td>{item.sku}</td>
                <td>{item.name}</td>
                <td>${item.price}</td>
                <td>{item.quantity}</td>
                <td>{item.consignorName}</td>
                <td>{item.status}</td>
                <td>{new Date(item.dateAdded).toLocaleDateString()}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
}

export default Inventory;
